sql.if:
AND INTERPRETER IN ( SELECT DISTINCT INTERPRETER FROM INTERPRETER_MULTI_CHOICE WHERE 1=1 AND TYPE_VALUE = 'category' AND FIELD IN
#{item}
)

mapper.insert:
INSERT INTO INTERPRETER (
    MEMBER, COUNTRY, DAY_UNIT, DAY_PRICE, CAR, PICKUP, PICKUP_UNIT, PICKUP_PRICE,
    INFO, PROFILE, CAREER, EXTRA, TIME_START, TIME_END, FILES, STATE
) VALUE (
    #{member}, #{country}, #{day_unit}, #{day_price}, #{car}, #{pickup}, #{pickup_unit}, #{pickup_price},
    #{info}, #{profile}, #{career}, #{extra}, #{time_start}, #{time_end}, #{files}, #{state}
)

mapper.update:
UPDATE INTERPRETER SET UPD_DATE = now()
,COUNTRY = #{country}
,DAY_UNIT = #{day_unit}
,DAY_PRICE = #{day_price}
,CAR = #{car}
,PICKUP = #{pickup}
,PICKUP_UNIT = #{pickup_unit}
,PICKUP_PRICE = #{pickup_price}
,INFO = #{info}
,PROFILE = #{profile}
,CAREER = #{career}
,EXTRA = #{extra}
,TIME_START = #{time_start}
,TIME_END = #{time_end}
,FILES = #{files}
,STATE = #{state}
,REJECT_MSG = #{reject_msg}
WHERE INTERPRETER = #{interpreter}

UPDATE INTERPRETER_RECOMMEND SET UPD_DATE = now()
,SORT = #{sort}
WHERE INTERPRETER_RECOMMEND = #{interpreter_recommend}
